contracts/TokenCrowdsale.sol.content:
// SPDX-License-Identifier: MIT 
pragma solidity >=0.4.24 <0.9.0;


import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/crowdsale/validation/CappedCrowdsale.sol";
import "@openzeppelin/contracts/crowdsale/Crowdsale.sol";


contract TokenCrowdsale is Crowdsale, CappedCrowdsale  {

  
    uint256 public investorMinCap = 30000000000000000;

    uint256 public investorMaxCap= 1000000000000000000;

    mapping(address => uint256 ) public contributions;

   uint256 public _weiRaised;

   
  
    event TokenPurchase (
           
        address indexed purchase,
        address indexed beneficiary,
        uint256 value,
        uint256 amount
    );

    constructor(uint256 _rate ,  address payable _wallet, ERC20 _token , uint256 _cap)  Crowdsale(_rate,_wallet, _token) CappedCrowdsale(_cap)
    public
      {
        require(_rate > 0);
        require(_wallet != address(0));

    }

    // ---------------------------------------
    // Crowdsale external interface 
    // ---------------------------------------

    // fallback public () external payable{}
    // receive() external payable{}



    // ---------------------------------------------------
    // internal interface (extensible)
    // ----------------------------------------------------


   function getUserContribution(address _beneficiary)
   public  returns (uint256){
    return contributions[_beneficiary];
   }

   function _preValidatePurchase(
    address _beneficiary,
    uint256 _weiAmount
  )
   internal view
   {
    require(_beneficiary != address(0));
    require(_weiAmount != 0);

    super._preValidatePurchase(_beneficiary, _weiAmount);
    uint256 _existingContribution = contributions[_beneficiary];
    uint256 _newContribution=_existingContribution.add(_weiAmount);
    require(_newContribution >= investorMinCap && _newContribution <= investorMaxCap);
    // contributions[_beneficiary] = _newContribution;
    this;
   }

}

